


#include <stdio.h>
#include <stdlib.h>

// Função para ordenar os dígitos de um número em ordem crescente
int ordenaDigitos(int num) {
    // Convertendo o número para uma string
    char str[10];
    sprintf(str, "%d", num);

    // Ordenando os dígitos da string
    for (int i = 0; str[i] != '\0'; i++) {
        for (int j = i + 1; str[j] != '\0'; j++) {
            if (str[j] < str[i]) {
                // Troca os dígitos
                char temp = str[i];
                str[i] = str[j];
                str[j] = temp;
            }
        }
    }

    // Convertendo a string de volta para um número
    return atoi(str);
}

// Função de comparação para a função qsort
int comparar(const void *a, const void *b) {
    return (*(int *)a - *(int *)b);
}

